mapper.select:
SELECT * FROM blog_posts b WHERE del_flag = '0'
and w.title like concat('%',#{searchName},'%')
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE blog_posts
SET
    title = #{blogPost.title},
    summary = #{blogPost.summary},
    content = #{blogPost.content}
WHERE blog_id = #{blogId}
